angular.module("SwitchWebUI.directives")
    .directive('alertItem', function () {
        return {
            restrict: 'E',
            template: '<div class="alertItem">' +
                '<div class="alertContainer {{data.badgeColor}}">' +
                '<div class="alertBadge"><healthicon value="data.severityIcon" /></div>' +
                '<div class="alertContent"><h3><strong>{{data.articleObj.message}}</strong></h3><p>{{data.articleObj.details}}</p></div>' +
                '</div>' +
                '</div>',
            scope: {
                article: "@"
            },
            transclude: false,
            replace: true,
            link: function ($scope, $element, $attrs) {
                $scope.data = {
                    articleObj: '',
                    badgeColor: 'primary',
                    severityIcon: '',
                    severity: '',
                    target: ''
                };

                //Local function

                function redraw() {
                    if ($scope.article != undefined) {
                        $scope.data.articleObj = JSON.parse($scope.article);
                        switch ($scope.data.articleObj["relatedtype"]) {
                            case "RESOURCE":
                                $scope.data.target = '#/Resources/' + $scope.data.articleObj['relatedid'] || '';
                                break;
                            case "LOG":
                            default:
                                $scope.data.target = '#/LogDetails/' + $scope.data.articleObj['relatedid'] || '';
                                break;
                        }

                        $scope.data.severityIcon = 'info';

                        switch ($scope.data.articleObj["severity"]) {
                            case 'critical':
                                $scope.data.badgeColor = 'critical';
                                $scope.data.severityIcon = 'red';
                                break;
                            case 'warning':
                                $scope.data.badgeColor = 'warning';
                                $scope.data.severityIcon = 'yellow';
                                break;
                            default:
                                $scope.data.badgeColor = 'info';
                                $scope.data.severityIcon = 'Informational';
                                break;
                        }
                    }
                };

                //Update when scope variables change
                $scope.$watch('article', function (v) { redraw(); });
                redraw();
            }
        };
    });
