angular.module('SwitchWebUI.directives')
    .directive('deviceicon', function () {
        return {
            restrict: 'E',
            template: '<span class="{{iconClass}}"></span>',
            scope: {
                deviceType: "=", //binding attribute will be: device-type [application, vm, cluster, server, storage]
                deviceState: "=" //binding attribute will be: device-state [on, off]
            },
            transclude: false,
            replace: true,
            link: function (scope, $element, $attrs) {
                function updateIcon() {
                    if (scope.deviceType != undefined) {
                        switch (scope.deviceType.toLowerCase()) {
                            case "storage":
                            case "equallogic":
                            case "compellent":
                            case "netapp":
                                scope.nodeType = "storage";
                                break;
                            case "server":
                            case "bladeserver":
                            case "rackserver":
                            case "chassis":
                                scope.nodeType = "server";
                                break;
                            case "cluster":
                            case "app":
                                scope.nodeType = "cluster";
                                break;
                            case "vm":
                            case "vcenter":
                            case "vmware":
                                scope.nodeType = "vm";
                                break;
                            case "application":
                            case "app":
                            default:
                                scope.nodeType = "application";
                                break;
                        }
                    } else {
                        scope.nodeType = "application";
                    }

                    if (scope.deviceState != undefined && !scope.deviceState) {
                        scope.isOn = false; //If a FALSE value is provided, set to OFF.
                    } else {
                        scope.isOn = true; //Default is ON.
                    }

                    scope.iconClass = "deviceicon " + scope.nodeType + (scope.isOn ? " on" : " off");
                }

                scope.iconClass = "";
                scope.isOn = false;
                scope.nodeType = "";

                //Update when health data changes
                scope.$watch('deviceType', function (value) { if (!value) return; else updateIcon(); });
                scope.$watch('deviceState', function (value) { if (value == undefined) return; else updateIcon(); });
            }
        };
    });

