angular.module('SwitchWebUI.directives')
    .directive('globalSearch', ['$rootScope', '$location', '$timeout', '$resource',
        function ($rootScope, $location, $timeout, $resource) {
            return {
                restrict: 'E',
                templateUrl: 'js/templates/globalSearch.html',
                scope: {
                },
                transclude: false,
                replace: true,
                link: function ($scope, $element, $attrs) {

                    var timeoutPromise;
                    var delayInMs = 650;

                    $scope.data = {
                        showResults: false,
                        noResults: false,
                        searchTerm: "",
                        searchResults: {}
                    };

                    $scope.actions = {
                        clearSearch: function (clearTerm) {
                            $scope.data.searchResults = {};
                            if (clearTerm) {
                                $scope.data.showResults = false;
                                $scope.data.searchTerm = "";
                                $scope.data.noResults = false;
                            }
                        },
                        searchChange: function () {

                            $timeout.cancel(timeoutPromise);

                            timeoutPromise = $timeout(function () {
                                $scope.actions.executeSearch();
                            }, delayInMs);

                        },
                        executeSearch: function () {
                            var currentTerm = $scope.data.searchTerm.trim();
                            if (currentTerm == undefined || currentTerm == "") {
                                $scope.actions.clearSearch(true);
                            }
                            else if (currentTerm.length < 3) {
                                return;
                            } else {
                                var searchService = $resource('api/Search');
                                searchService.get({ id: currentTerm, limit: 5 }).$promise.then(function (results) {
                                    $scope.data.showResults = true;

                                    if (results != undefined && (results.categories != undefined && results.categories.length > 0)) {
                                        $scope.data.searchResults = results;
                                        $scope.data.showResults = true;
                                        $scope.data.noResults = false;
                                    } else {
                                        $scope.actions.clearSearch(false);
                                        $scope.data.showResults = true;
                                        $scope.data.noResults = true;
                                    }
                                }).catch(function (error) {
                                    $scope.actions.clearSearch(false);
                                });
                            }
                        }
                    };

                    $scope.$watch('data.searchTerm', $scope.actions.searchChange);
                    $rootScope.$on('$locationChangeStart', function () { $scope.actions.clearSearch(true); });
                }
            };
        }]);

