angular.module("SwitchWebUI.directives")
    .directive("portPanel", ["$rootScope", function ($rootScope) {

        function getToolTip(item) {
            return "Port ID: " + item.ID +
                "<br>Port Status: " + item.PortState +
                "<br>Port Type: " + item.ConnectorType +
                "<br>VLAN: " + item.VLANType +
                "<br>LAG: " + (item.LagMembership ? item.LagMembership : "None") +
                "<br>Profile: " + (item.Profile ? "P" : "None") +
                "<br><a>&gt; More</a>";
        }

        function getTemplate(panelType, port) {
            var template = {};
            template.id = "/portboxsquare.html";

            switch (panelType) {
                case "Port":
                    if (port.PortState == "Up") {
                        template.boxClass = "port-box-green";
                    } else if (port.PortState == "disabled") {
                        template.boxClass = "port-box-gray";
                    } else if (port.PortState == "Inactive") {
                        template.boxClass = "port-box-text";
                    } else {
                        template.boxClass = "glyphicon glyphicon-remove red";
                    }
                    break;
                case "VLAN":
                    template.boxClass = "port-box-text";
                    template.displayChar = port.VLANType == "Access" ? "A" : (port.VLANType == "Trunk" ? "T" : (port.VLANType == "General" ? "G" : "O"));
                    break;
                case "LAG":
                    template.boxClass = "port-box-text";
                    template.displayChar = port.LagMembership ? port.LagMembership : "";
                    break;
                case "Profile":
                    template.boxClass = "port-box-text";
                    template.displayChar = port.Profile ? "P" : "";
                    break;
            }


            return template;
        }

        function createLegends(panelType) {
            var legends = [];
            switch (panelType) {
                case "Port":
                    legends = [
                        { description: "Up", template: { id: "/portboxsquare.html", boxClass: "port-box-green" } },
                        { description: "Inactive", template: { id: "/portboxsquare.html", boxClass: "port-box-text" } },
                        {
                            description: "Error", template: {
                                id: "/portboxsquare.html", boxClass: "glyphicon glyphicon-remove red", iconColor: "red"
                            }
                        },
                        { description: "Disabled", template: { id: "/portboxsquare.html", boxClass: "port-box-gray" } }
                    ];
                    break;
                case "VLAN":
                    legends = [
                        { description: "Access", template: { id: "/portboxsquare.html", boxClass: "port-box-text", displayChar: "A" } },
                        { description: "General", template: { id: "/portboxsquare.html", boxClass: "port-box-text", displayChar: "G" } },
                        { description: "Trunk", template: { id: "/portboxsquare.html", boxClass: "port-box-text", displayChar: "T" } },
                        { description: "Other", template: { id: "/portboxsquare.html", boxClass: "port-box-text", displayChar: "O" } }
                    ];
                    break;
                case "LAG":
                    legends = [
                       { description: "LAG ID", template: { id: "/portboxsquare.html", boxClass: "port-box-text", displayChar: "#" } },
                       { description: "None", template: { id: "/portboxsquare.html", boxClass: "port-box-text" } }
                    ];
                    break;
                case "Profile":
                    legends = [
                       { description: "Profile", template: { id: "/portboxsquare.html", boxClass: "port-box-text", displayChar: "P" } },
                       { description: "None", template: { id: "/portboxsquare.html", boxClass: "port-box-text" } }
                    ];
                    break;
            }
            return legends;
        }

        return {
            template: "<ng-include src='getTemplateUrl()'/>",
            scope: {
                panelType: "@",
                panel: "="
            },
            link: function (scope) {

                scope.getTemplateUrl = function () {
                    return $rootScope.switchType.template;
                };

                scope.$watch("panel", function (panel) {

                    if (!panel) return;

                    var ports = angular.copy(panel.Ports);

                    angular.forEach(ports, function (value, key) {
                        value.template = getTemplate(scope.panelType, value);
                        value.toolTip = getToolTip(value);
                    });

                    var totalRows = $rootScope.switchType.rows;

                    scope.rows = [];
                    for (var i = 0; i < totalRows; i++) {
                        scope.rows.push([]);
                    }

                    for (var i = 0; i < ports.length; ++i) {
                        var mod = i % totalRows;
                        scope.rows[mod].push(ports[i]);
                    }

                    scope.legends = createLegends(scope.panelType);
                });
            }

        };
    }]);
