angular.module('Clarity.directives')
    .directive('showTime', function () {
        return {
            restrict: 'E',
            template: '<span>{{displayTime}}</span>',
            scope: {
                time: "=?", //binding attribute will be: time
                timeFormat: "=?", //binding attribute will be: time-format
                fromDate: "=?" //binding attribute will be: from-date
            },
            transclude: false,
            replace: true,
            link: function ($scope, $element, $attrs) {
                //Local function
                function updateTime() {
                    var _time = new Date(); //Default to NOW if not set.
                    var _format = "";
                    var _fromDate = null;
                    if ($scope.time && moment($scope.time).isValid()) _time = $scope.time; else _time = new Date();
                    if ($scope.timeFormat) _format = $scope.timeFormat;
                    if ($scope.fromDate) _fromDate = $scope.fromDate;

                    if (moment(_time).isValid()) {
                        if (_format != "") {
                            // Format the date using the specified format
                            $scope.displayTime = moment(_time).format(_format);
                        }
                        else {
                            if (_fromDate == undefined || _fromDate == "") _fromDate = new Date();
                            // No format was specified, so humanize the date (e.g. "5 days ago")
                            $scope.displayTime = moment(_time).from(_fromDate, false);
                        }
                    }
                };

                //Update when scope variables change
                $scope.$watch('time', function (value) { if (!value) return; else updateTime(); });
                $scope.$watch('timeFormat', function (value) { if (!value) return; else updateTime(); });
                updateTime();
            }
        };
    });
///#source 1 1 /js/directives/sunburst.js
angular.module("SwitchWebUI.directives")
    .directive('sunburst', [
        function () {
            return {
                restrict: 'E',
                scope: {
                    data: '='
                },
                link: function (scope, element) {

                    // Dimensions of sunburst.
                    var width = 400,
                        height = width,
                        radius = width / 2,
                        x = d3.scale.linear().range([0, 2 * Math.PI]),
                        y = d3.scale.pow().exponent(1.3).domain([0, 1]).range([0, radius]),
                        padding = 15,
                        duration = 1000;

                    // Breadcrumb dimensions: width, height, spacing, width of tip/tail.
                    var b = {
                        w: 75, h: 30, s: 3, t: 10
                    };

                    // Total size of all segments; we set this later, after loading the data.
                    var totalSize = 0;

                    var vis = d3.select(element[0]).append("svg:svg")
                        .attr("width", width)
                        .attr("height", height)
                        .append("svg:g")
                        .attr("id", "container")
                        .attr("transform", "translate(" + width / 2 + "," + height / 2 + ")");

                    //var partition = d3.layout.partition()
                    //    .size([2 * Math.PI, radius * radius])
                    //    .value(function (d) { return d.size; });

                    var partition = d3.layout.partition()
                        .sort(null)
                        .value(function (d) { return 5.8 - d.depth; });

                    //var arc = d3.svg.arc()
                    //    .startAngle(function (d) { return d.x; })
                    //    .endAngle(function (d) { return d.x + d.dx; })
                    //    .innerRadius(function (d) { return Math.sqrt(d.y); })
                    //    .outerRadius(function (d) { return Math.sqrt(d.y + d.dy); });

                    var arc = d3.svg.arc()
                        .startAngle(function (d) { return Math.max(0, Math.min(2 * Math.PI, x(d.x))); })
                        .endAngle(function (d) { return Math.max(0, Math.min(2 * Math.PI, x(d.x + d.dx))); })
                        .innerRadius(function (d) { return Math.max(0, d.y ? y(d.y) : d.y); })
                        .outerRadius(function (d) { return Math.max(0, y(d.y + d.dy)); });

                    // Use d3.text and d3.csv.parseRows so that we do not need to have a header
                    // row, and can receive the csv as an array of arrays.
                    //d3.text("visit-sequences.csv", function (text) {
                    //    var csv = d3.csv.parseRows(text);
                    //    var json = buildHierarchy(csv);
                    //    createVisualization(json);
                    //});

                    // Main function to draw and set up the visualization, once we have the data.
                    function createVisualization(json) {

                        // Basic setup of page elements.
                        initializeBreadcrumbTrail();

                        // Bounding circle underneath the sunburst, to make it easier to detect
                        // when the mouse leaves the parent g.
                        vis.append("svg:circle")
                            .attr("r", radius)
                            .style("opacity", 0);

                        vis.append("svg:circle")
                            .attr("r", radius / 4)
                            .style("fill", "#8C97B7")
                            .style("opacity", 1);

                        // For efficiency, filter nodes to keep only those large enough to see.
                        var nodes = partition.nodes(json)
                            .filter(function (d) {
                                return (d.dx > 0.005); // 0.005 radians = 0.29 degrees
                            });

                        var path = vis.data([json]).selectAll("path").data(nodes);

                        path.enter().append("svg:path")
                            .attr("display", function (d) { return d.depth ? null : "none"; })
                            .attr("d", arc)
                            .attr("fill-rule", "evenodd")
                            .style("fill", function (d) {
                                return d.color || "#fff";
                            })
                            .style("opacity", 1)
                            .on("mouseover", mouseover);

                        path.enter().append("svg:path")
                            .attr("display", function (d) { return d.depth ? null : "none"; })
                            .attr("d", arc)
                            .attr("fill", "transparent")
                            .style("stroke", "#fff")
                            .style("stroke-width", "2")
                            .style("opacity", 1)
                            .on("mouseover", mouseover);

                        var text = vis.selectAll("text").data(nodes);
                        var textEnter = text.enter().append("text")
                            .style("fill-opacity", 1)
                            .style("fill", function (d) {
                                return "#fff";
                            })
                            .attr("text-anchor", function (d) {
                                return x(d.x + d.dx / 2) > Math.PI ? "end" : "start";
                            })
                            .attr("dy", ".3em")
                            .attr("transform", function (d) {
                                //var multiline = (d.title || "").split(" ").length > 1,
                                //    angle = x(d.x + d.dx / 2) * 180 / Math.PI - 90,
                                //    rotate = angle + (multiline ? -.5 : 0);
                                var angle = x(d.x + d.dx / 2) * 180 / Math.PI - 90;
                                var rotate = angle;

                                if (d.depth == 0) {
                                    return "rotate(0) translate(" + (padding * -1) + ") rotate(0)";
                                }

                                if (d.depth == 1) {
                                    return "rotate(" + rotate + ")translate(" + (y(d.y) + padding) + ")rotate(" + (rotate * -1) + ")";
                                }
                                return "rotate(" + rotate + ")translate(" + (y(d.y) + padding) + ")rotate(" + (angle > 90 ? -180 : 0) + ")";
                            })
                            .on("click", click);

                        textEnter.append("tspan")
                            .attr("x", 0)
                            .text(function (d) {
                                return d.title;
                            });

                        //textEnter.append("tspan")
                        //    .attr("x", 0)
                        //    .attr("dy", "1em")
                        //    .text(function (d) {
                        //        return d.depth ? d.title || "" : "";
                        //    });


                        // Add the mouseleave handler to the bounding circle.
                        d3.select("#container").on("mouseleave", mouseleave);

                        // Get total size of the tree = value of root node from partition.
                        totalSize = path.node().__data__.value;

                        function click(d) {
                            path.transition()
                              .duration(duration)
                              .attrTween("d", arcTween(d));

                            // Somewhat of a hack as we rely on arcTween updating the scales.
                            text.style("visibility", function (e) {
                                return isParentOf(d, e) ? null : d3.select(this).style("visibility");
                            })
                              .transition()
                                .duration(duration)
                                .attrTween("text-anchor", function (d) {
                                    return function () {
                                        return x(d.x + d.dx / 2) > Math.PI ? "end" : "start";
                                    };
                                })
                                .attrTween("transform", function (d) {
                                    var multiline = (d.title || "").split(" ").length > 1;
                                    return function () {
                                        var angle = x(d.x + d.dx / 2) * 180 / Math.PI - 90,
                                            rotate = angle + (multiline ? -.5 : 0);
                                        return "rotate(" + rotate + ")translate(" + (y(d.y) + padding) + ")rotate(" + (angle > 90 ? -180 : 0) + ")";
                                    };
                                })
                                .style("fill-opacity", function (e) { return isParentOf(d, e) ? 1 : 1e-6; })
                                .each("end", function (e) {
                                    d3.select(this).style("visibility", isParentOf(d, e) ? null : "hidden");
                                });
                        }

                    };

                    // Fade all but the current sequence, and show it in the breadcrumb trail.
                    function mouseover(d) {

                        if (!d) return;

                        var percentage = (100 * d.value / totalSize).toPrecision(3);
                        var percentageString = percentage + "%";
                        if (percentage < 0.1) {
                            percentageString = "< 0.1%";
                        }

                        d3.select("#percentage")
                            .text(percentageString);

                        d3.select("#explanation")
                            .style("visibility", "");

                        var sequenceArray = getAncestors(d);
                        updateBreadcrumbs(sequenceArray, percentageString);

                        // Fade all the segments.
                        d3.selectAll("path")
                            .style("opacity", 0.3);

                        // Then highlight only those that are an ancestor of the current segment.
                        vis.selectAll("path")
                            .filter(function (node) {
                                return (sequenceArray.indexOf(node) >= 0);
                            })
                            .style("opacity", 1);
                    }

                    // Restore everything to full opacity when moving off the visualization.
                    function mouseleave(d) {

                        // Hide the breadcrumb trail
                        d3.select("#trail")
                            .style("visibility", "hidden");

                        // Deactivate all segments during transition.
                        d3.selectAll("path").on("mouseover", null);

                        // Transition each segment to full opacity and then reactivate it.
                        d3.selectAll("path")
                            .transition()
                            .duration(1000)
                            .style("opacity", 1)
                            .each("end", function () {
                                d3.select(this).on("mouseover", mouseover);
                            });

                        d3.select("#explanation")
                            .style("visibility", "hidden");
                    }

                    // Given a node in a partition layout, return an array of all of its ancestor
                    // nodes, highest first, but excluding the root.
                    function getAncestors(node) {
                        var path = [];
                        var current = node;
                        while (current.parent) {
                            path.unshift(current);
                            current = current.parent;
                        }
                        return path;
                    }

                    function initializeBreadcrumbTrail() {
                        // Add the svg area.
                        var trail = d3.select("#sequence").append("svg:svg")
                            .attr("width", width)
                            .attr("height", 50)
                            .attr("id", "trail");
                        // Add the label at the end, for the percentage.
                        trail.append("svg:text")
                          .attr("id", "endlabel")
                          .style("fill", "#000");
                    }

                    // Generate a string that describes the points of a breadcrumb polygon.
                    function breadcrumbPoints(d, i) {
                        var points = [];
                        points.push("0,0");
                        points.push(b.w + ",0");
                        points.push(b.w + b.t + "," + (b.h / 2));
                        points.push(b.w + "," + b.h);
                        points.push("0," + b.h);
                        if (i > 0) { // Leftmost breadcrumb; don't include 6th vertex.
                            points.push(b.t + "," + (b.h / 2));
                        }
                        return points.join(" ");
                    }

                    // Update the breadcrumb trail to show the current sequence and percentage.
                    function updateBreadcrumbs(nodeArray, percentageString) {

                        // Data join; key function combines name and depth (= position in sequence).
                        var g = d3.select("#trail")
                            .selectAll("g")
                            .data(nodeArray, function (d) { return d.title + d.depth; });

                        // Add breadcrumb and label for entering nodes.
                        var entering = g.enter().append("svg:g");

                        entering.append("svg:polygon")
                            .attr("points", breadcrumbPoints)
                            .style("fill", function (d) { return d.color || "#fff"; });

                        entering.append("svg:text")
                            .attr("x", (b.w + b.t) / 2)
                            .attr("y", b.h / 2)
                            .attr("dy", "0.35em")
                            .style("fill", "#fff")
                            .attr("text-anchor", "middle")
                            .text(function (d) { return d.title; });

                        // Set position for entering and updating nodes.
                        g.attr("transform", function (d, i) {
                            return "translate(" + i * (b.w + b.s) + ", 0)";
                        });

                        // Remove exiting nodes.
                        g.exit().remove();

                        // Now move and update the percentage at the end.
                        d3.select("#trail").select("#endlabel")
                            .attr("x", (nodeArray.length + 0.5) * (b.w + b.s))
                            .attr("y", b.h / 2)
                            .attr("dy", "0.35em")
                            .attr("text-anchor", "middle")
                            .text(percentageString);

                        // Make the breadcrumb trail visible, if it's hidden.
                        d3.select("#trail")
                            .style("visibility", "");

                    }

                    // Take a 2-column CSV and transform it into a hierarchical structure suitable
                    // for a partition layout. The first column is a sequence of step names, from
                    // root to leaf, separated by hyphens. The second column is a count of how 
                    // often that sequence occurred.
                    function buildHierarchy(csv) {
                        var root = { "name": "root", "children": [] };
                        for (var i = 0; i < csv.length; i++) {
                            var sequence = csv[i][0];
                            var size = +csv[i][1];
                            if (isNaN(size)) { // e.g. if this is a header row
                                continue;
                            }
                            var parts = sequence.split("-");
                            var currentNode = root;
                            for (var j = 0; j < parts.length; j++) {
                                var children = currentNode["children"];
                                var nodeName = parts[j];
                                var childNode;
                                if (j + 1 < parts.length) {
                                    // Not yet at the end of the sequence; move down the tree.
                                    var foundChild = false;
                                    for (var k = 0; k < children.length; k++) {
                                        if (children[k]["name"] == nodeName) {
                                            childNode = children[k];
                                            foundChild = true;
                                            break;
                                        }
                                    }
                                    // If we don't already have a child node for this branch, create it.
                                    if (!foundChild) {
                                        childNode = { "name": nodeName, "children": [] };
                                        children.push(childNode);
                                    }
                                    currentNode = childNode;
                                } else {
                                    // Reached the end of the sequence; create a leaf node.
                                    childNode = { "name": nodeName, "size": size };
                                    children.push(childNode);
                                }
                            }
                        }
                        return root;
                    };

                    function isParentOf(p, c) {
                        if (p === c) return true;
                        if (p.children) {
                            return p.children.some(function (d) {
                                return isParentOf(d, c);
                            });
                        }
                        return false;
                    }

                    // Interpolate the scales!
                    function arcTween(d) {
                        var my = maxY(d),
                            xd = d3.interpolate(x.domain(), [d.x, d.x + d.dx]),
                            yd = d3.interpolate(y.domain(), [d.y, my]),
                            yr = d3.interpolate(y.range(), [d.y ? 20 : 0, radius]);
                        return function (d) {
                            return function (t) { x.domain(xd(t)); y.domain(yd(t)).range(yr(t)); return arc(d); };
                        };
                    }

                    function maxY(d) {
                        return d.children ? Math.max.apply(Math, d.children.map(maxY)) : d.y + d.dy;
                    }

                    //scope.render = function (data) {
                    createVisualization(scope.data);
                    //};

                    //scope.$watch('data', function () {
                    //    scope.render(scope.data);
                    //}, true);



                }
            };
        }
    ]);

