angular.module('SwitchWebUI.directives')
    .directive('stateicon', function () {
        return {
            restrict: 'E',
            template: '<div><span class="{{iconClass}}">' +
                        '<span icon="{{iconName}}"></span>' +
                        '</span>&nbsp;<span>{{displayText}}</span></div>',
            scope: {
                state: "=value"
                //binding attribute will be: value
            },
            transclude: false,
            replace: true,
            link: function (scope, $element, $attrs) {
                function updateIcon() {
                    switch (scope.state) {
                        case "available":
                            scope.iconName = "available";
                            scope.iconClass = "stateicon success ci ci-state-ok-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Available');
                        case "online":
                            scope.iconName = "online";
                            scope.iconClass = "stateicon success ci ci-state-ok-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Online') ;
                            break;
                        case "deployed":
                            scope.iconName = "deployed";
                            scope.iconClass = "stateicon info ci ci-nav-deploy-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Deployed');
                            break;
                        case "unknown":
                            scope.iconName = "unknown";
                            scope.iconClass = "stateicon question ci ci-state-ok-core " + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Unknown');
                            break;
                        case "unmanaged":
                            scope.iconName = "unmanaged";
                            scope.iconClass = "stateicon question ci ci-state-ok-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Unmanaged');
                            break;
                        case "errors":
                            scope.iconName = "errors";
                            scope.iconClass = "stateicon critical ci ci-state-critical-core " + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Error');
                            break;
                        case "pending":
                            scope.iconName = "pending";
                            scope.iconClass = "ci ci-action-restore-reset-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Pending');
                            break;
                        case "deploying":
                            scope.iconName = "deploying";
                            scope.iconClass = "ci ci-action-restore-reset-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_Deploying');
                            break;
                        case "poweringoff":
                            scope.iconName = "poweringoff";
                            scope.iconClass = "ci ci-action-restore-reset-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_PoweringOn');
                            break;
                        case "poweringon":
                            scope.iconName = "poweringon";
                            scope.iconClass = "ci ci-action-restore-reset-core" + (scope.transparent ? "" : " solid");
                            scope.displayText = $translate.instant('GENERIC_PoweringOff');
                            break;
                    }
                }
                //Update when state data changes
                scope.$watch('state', function (value) { if (!value) return; else updateIcon(); });
            }
        };
    });

