angular.module('SwitchWebUI.directives')
    .directive('rangeValidator',
        ['$rootScope', '$window', '$location', '$timeout', '$http', 'GlobalServices', 'Commands',
            function ($rootScope, $window, $location, $timeout, $http, GlobalServices, Commands) {
                return {
                    restrict: 'A',
                    require: 'ngModel',
                    scope: {
                        minVal: "=",
                        maxVal: "=",
                        multipleRequired: "="
                    },
                    link: function (scope, element, attrs, ngModel) {
                        var validVals = /^[0-9 ,-]+$/i;
                        ngModel.$validators.rangeValidator = function (stringInput) {
                            console.log(parseInt(stringInput));
                            if (scope.multipleRequired && !isNaN(stringInput)) {
                                return false;
                            }
                            //return false if invalid character entered
                            if (!stringInput.match(validVals)) {
                                return false;
                            } 
                            //convert numbers to array
                            var result = stringInput.split(/[ ,-]+/).filter(function (v) { return v !== '' });
                            var inputIsValid = true;
                            angular.forEach(result, function (number) {
                                var intVal = parseInt(number);
                                if (intVal < scope.minVal || intVal > scope.maxVal) { inputIsValid = false }
                            });
                            return inputIsValid;
                        }
                    }
                };
            }]);


