angular.module('SwitchWebUI.directives')
    .directive('ipurlValidator',
        ['$rootScope', '$window', '$location', '$timeout', '$http', 'GlobalServices', 'Commands',
            function ($rootScope, $window, $location, $timeout, $http, GlobalServices, Commands) {
                return {
                    restrict: 'A',
                    require: 'ngModel',
                    link: function (scope, element, attrs, ngModel) {
                        var ipPattern = /\b(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\b/;
                        var urlPattern = new RegExp('^(https?:\\/\\/)?' +                               // protocol
                                                '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|' +     // domain name
                                                '((\\d{1,3}\\.){3}\\d{1,3}))' +                          // OR ip (v4) address
                                                '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*' +                      // port and path
                                                '(\\?[;&a-z\\d%_.~+=-]*)?' +                             // query string
                                                '(\\#[-a-z\\d_]*)?$', 'i');                              // fragment locator
                        ngModel.$validators.ipurlValidator = function (stringInput) {
                            if (!stringInput) return true;
                            if (!stringInput.match(ipPattern)) {
                                if (!urlPattern.test(stringInput)) {
                                    // not IP or URL
                                    return false;
                                } else {
                                    // URL ok
                                    return true;
                                }
                            } else {
                                // IP ok
                                return true;
                            }

                        }
                    }
                };
            }]);

