angular.module('app')
    .controller('AppController',
        ['$rootScope', '$window', '$http', '$templateCache', '$scope', '$log', '$translate', 'GlobalServices', '$location', 'Commands', 'alert',
            function ($rootScope, $window, $http, $templateCache, $scope, $log, $translate, GlobalServices, $location, Commands, Alert) {

                $scope.navigation = GlobalServices.navigation;
                $scope.$on("navigationUpdated", function (evt, navigation) {
                    $scope.navigation = navigation;
                });

                

                $scope.updateNavigation = function (menuItems) {
                    var newMenu = [];
                    //Add the first menu item (Home) to the top.  If there is only one Global menu item, save it for the end.
                    if (GlobalServices.navigation.length >= 2) newMenu.push(GlobalServices.navigation[0]);
                    menuItems.sort(function (a, b) {
                        var compare = 0;
                        if (a['order'] == undefined || b['order'] == undefined) compare = 0;
                        else compare = a['order'] - b['order'];
                        if (compare == 0) {
                            if (a['label'] < b['label']) compare = -1;
                            if (a['label'] > b['label']) compare = 1;
                        }
                        return compare;
                    });
                    newMenu = newMenu.concat(menuItems);
                    //Add any other global menu items to the end (start with 0 if there is only 1, start with 1 if there are at least two)
                    for (var i = (GlobalServices.navigation.length >= 2) ? 1 : 0; i < GlobalServices.navigation.length; i++) {
                        newMenu.push(GlobalServices.navigation[i]);
                    }
                    $scope.navigation = newMenu;
                    if ($location.path() == undefined || $location.path() == "") {
                        var newHash = $scope.navigation[0]['href'];
                        if (newHash.indexOf("#") == 0) newHash = newHash.slice(1, newHash.length); //Strip off the leading hash
                        if (newHash.indexOf("/") != 0) newHash = "/" + newHash; //Ensure path starts with a slash
                        $location.path(newHash);
                    }
                };

                $scope.get_ioa_mode = function () {
                    Alert.flush();

                    $http.post('/webuiclient', { command: Commands.get_ioa_properties })
                        .success(function (data, status, headers, config) {
                            $rootScope.unit = data.unit;
                            $rootScope.ioaMode = data.ioaMode;
			    $rootScope.portQuad = data.portQuad;
                            $rootScope.model = $rootScope.SwitchWebUI.modelToType(data.model);
                            $rootScope.modelName = data.model;
                            $rootScope.fwVersion = data.fwVersion;
                            $rootScope.$broadcast("initialPropertiesLoaded");
                        })
                        .error(function (data, status, headers, config) {
                            var error = (config && config.response) ? config.response['return-msg'] : 'Unknown Error';
                            $log.error(angular.toJson('Error loading dashboard - ' + error));
                            Alert({
                                label: $translate.instant('ERROR_GetIOAProperties'),
                                content: angular.toJson(error),
                                type: 'danger',
                                location: 'global-alerts'
                            });
                        });
                };

                $scope.about = function () {
                    //aboutBox.modal.show();
                };

                $scope.help = function () {
                    GlobalServices.showHelp();
                };

                $scope.logout = function () {
                    
                    $http.post('/webuiclient', { command: Commands.logout })
                        .success(function (data, status, headers, config) {
                            delete $window.sessionStorage.token;
                            $window.location.href = '/';
                        })
                        .error(function (data, status, headers, config) {
                            delete $window.sessionStorage.token;
                            $window.location.href = '/';
                        });
                };

                $scope.initialize = function() {
                    $scope.get_ioa_mode();
                    $rootScope.userType = $window.sessionStorage.userType;
                };
                
                $scope.initialize();

            }]);

