angular.module('app-login')
    .controller('AppLoginController',
        ['$window', '$scope', '$log', '$http', '$translate', 'localStorageService', 'Commands',
            function ($window, $scope, $log, $http, $translate, localStorage, Commands) {

                $scope.session = {};
                $scope.verifyingCredentials = false;
                $scope.failedLogin = false;
                $scope.message = '';
                $scope.shouldNextButtonBeEnabled = false;

                var rememberUser = function (userdata) {
                    if ($scope.data.settings.rememberMe) {
                        localStorage.set('SwitchWebUI.currentUser', userdata.username);
                    } else {
                        localStorage.remove('SwitchWebUI.currentUser');
                    }
                };

                var currentUser = localStorage.get('SwitchWebUI.currentUser');
                $scope.data = {
                    settings: {
                        applicationName: $translate.instant('ApplicationTitle'),
                        messageText: "",
                        username: currentUser == undefined ? "" : currentUser,
                        signInLabel: $translate.instant('GENERIC_Login'),
                        showMessage: false,
                        showDomain: false,
                        showRememberMe: false
                    }
                };

                $scope.actions = {
                    clickLogin: function (userdata) {
                        rememberUser(userdata);

                        $scope.session.id = "";
                        $scope.session.username = userdata.username;
                        $scope.session.password = userdata.password;
                        $scope.session.type = '';
                        $scope.session.hideGettingStarted = false;

                        $scope.data.settings.isSpinning = true;

                        $scope.verifyingCredentials = true;

                        $http.post('/webuiclient', { command: Commands.login, username: userdata.username, password: userdata.password })
                            .success(function (data, status, headers, config) {

                                if (data.goToLoader) {
                                    window.location = "dataloader.html";
                                } else {

                                    $window.sessionStorage.token = data.sessionid;
                                    $window.sessionStorage.currentUser = userdata.username;

                                    $window.sessionStorage.userType = data['user-type'];

                                    $scope.verifyingCredentials = false;

                                    var url = 'index.html';

                                    $scope.data.settings.showMessage = false;
                                    $scope.data.settings.isSpinning = false;

                                    if (data['start-gsw']=="yes") {
                                        window.location = 'index.html#/dashboard/wizard';
                                    } else {
                                        window.location = 'index.html';
                                    }

                                    //$window.location = url;
                                    //$window.location.reload();
                                }

                            })
                            .error(function (data, status, headers, config) {

                                delete $window.sessionStorage.token;

                                var error = (config && config.response) ? config.response['return-msg'] : $translate.instant('GENERIC_LoginFailed');

                                $scope.data.settings.isSpinning = false;
                                $scope.failedLogin = true;
                                $scope.data.settings.showMessage = true;
                                $scope.data.settings.messageText = error;

                            });

                    }
                };
            }]);

